var util = require("util");

function createHtmlNode(tag) {
    var num = parseInt(Math.random() * 10000);
    var id = util.format("html-node-%d", num);
    var node = document.createElement(tag || "div");
    return node;
}

function loading() {
    var loadingSrc = "data:image/gif;base64,";
    var div = createHtmlNode("div");
    var img = createHtmlNode("img");
    divStyle = "position: fixed; top: 0; left: 0; width: 100%; height: 100%; z-index:100; background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNgmAkAAJwAmsoF3fMAAAAASUVORK5CYII=);";
    imgStyle = "margin: auto; position: absolute; top: 0; left: 0; bottom: 0; right: 0;";
    img.setAttribute("src", loadingSrc);
    img.setAttribute("style", imgStyle);
    div.setAttribute("style", divStyle);
    div.appendChild(img);
    document.body.appendChild(div);
    return div;
}

exports.loading = loading;